import React from 'react';
import { useEffect, useState } from 'react';
import api from './Api/Api';
import { CardList } from './Components/CardItem';
import CategoryItems from './Components/CategoryItems';
import Button from './Components/common/Button';
import Modal from './Components/common/Modal';
import Layout from './Components/Layout';

function App() {

  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [storageData, setStorageData] = useState(null);

  useEffect(() => {
    handleFetch();
  }, [])

  const handleFetch = async () => {
    try {
      await api.getBallotData()
        .then(res => {
          setData(res.items);
          console.log(res.items);
        })
    } catch (error) {
      setError(error);
    }
  }

  const handleShowModal = () => {
    const storage = JSON.parse(localStorage.getItem("selected"));
    if (storage) {
      setStorageData(storage);
      setShowModal(true);
    }
  }

  return (
    <Layout>

      {/* list categories */}
      {!error ?
        data?.map((item, index) =>
          <div key={index}>
            <CategoryItems item={item} />
          </div>
        )
        :
        <div>Data not Found</div>
      }

      {/* submit button */}
      <div className='text-center sticky bottom-0 py-8'>
        <Button
          className='py-4 px-16 rounded-lg bg-green-persia hover:bg-keppel border-2 text-white text-xl lg:text-2xl font-black uppercase'
          onClick={handleShowModal}
        >
          Submit
        </Button>
      </div>

      {/* show modal */}
      <Modal
        isShow={showModal}
        title="Selected Nominee"
        onClose={() => setShowModal(false)}
      >
        <div className='divide-y'>
          {
            storageData?.map((item, index) =>
              <div key={index} className='py-4'>
                <CardList item={item} list />
              </div>
            )
          }
        </div>
      </Modal>

    </Layout>
  );
}

export default App;
